// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC20 {
    function transferFrom(address from, address to, uint256 value) external returns (bool);
}

/**
 * @title MinimalEscrow
 * @dev Extremely light Escrow for Shardeum limits.
 */
contract Escrow {
    IERC20 public trustToken;
    address public owner;

    event FundsLocked(string projectId, address indexed client, uint256 amount);

    struct Project {
        uint256 amount;
        address client;
        bool active;
    }

    mapping(string => Project) public projects;

    constructor(address _tokenAddress) {
        trustToken = IERC20(_tokenAddress);
        owner = msg.sender;
    }

    function lockFunds(string calldata projectId, uint256 amount) external {
        require(amount > 0, "Zero amount");
        require(!projects[projectId].active, "Exists");

        // Transfer tokens
        require(trustToken.transferFrom(msg.sender, address(this), amount), "Failed");

        projects[projectId] = Project({
            amount: amount,
            client: msg.sender,
            active: true
        });

        emit FundsLocked(projectId, msg.sender, amount);
    }
}

